mapper.select:
SELECT ID, CLIP_ID, TITLE, CREATOR_NAME, VIEW_COUNT, CREATED_AT, STREAMER_ID
FROM twitch_clip
WHERE ID = #{id}

mapper.update:
UPDATE twitch_clip
SET CLIP_ID = #{clipId}, TITLE = #{title}, CREATOR_NAME = #{creatorName}, VIEW_COUNT = #{viewCount}, CREATED_AT = #{createdAt}, STREAMER_ID = #{streamerId}
WHERE ID = #{id}
